/**
 * @file Button Component
 *
 * @author Leo Wang(wangkemiao@zhihu.com)
 */

import omit from 'lodash.omit'
import React, {Component} from 'react'
import PropTypes from 'prop-types'
import cx from 'classnames'

import {CLASS_PREFIX} from '../util/config'
import connectControl from '../Form/connectControl'

import './index.css'

/**
 * 按钮组件
 *
 * @export
 * @class Button
 * @extends {Component}
 */
@connectControl
export default class Button extends Component {

  /**
   * 组件支持传入的 props 定义，继承了 {@link Form.Control#propTypes} 指定的会传入真实控件的 props，故在此只介绍私有的 props
   *
   * @property {string} [type='button'] 类型，可选值同 html button 的 type 取值： button | submit | reset | menu（暂未实现）
   * @property {function(e:Event)} onClick 点击事件句柄
   */
  static propTypes = {
    type: PropTypes.string,
    onClick: PropTypes.func
  }

  /**
   * 默认的 props
   *
   * @static
   * @memberof Button
   */
  static defaultProps = {
    type: 'button',
    onClick(e) {
      e.preventDefault()
    }
  }

  /**
   * 点击行为处理
   *
   * @memberof Button
   *
   * @param {React.MouseEvent} e 事件参数
   */
  handleClick = e => {
    const {onClick} = this.props
    onClick(e)
  }

  render() {
    const {
      id,
      children,
      status,
      ...resetProps
    } = this.props

    const passThroughProps = omit(resetProps, ['onClick', 'onValueChange'])

    const classes = cx(
      `${CLASS_PREFIX}Button`,  // 控件样式
      `${CLASS_PREFIX}Button--is-${status}`  // 状态样式
    )
    return (
      <button
        id={id}
        name={id}  // name 与 id 相同
        {...passThroughProps}
        className={classes}
        onClick={this.handleClick}
      >
        {children}
      </button>
    )
  }
}

/**
 * @file FormGroup 组件，用于对表单元素做分组布局管理
 *
 * @author Leo Wang(wangkemiao@zhihu.com)
 */

import React, {Component} from 'react'
import PropTypes from 'prop-types'
import cx from 'classnames'

import {CLASS_PREFIX, LAYOUT} from '../util/config'

import './Group.css'

/**
 * FormGroup 组件，用于对表单元素做分组布局管理
 *
 * @export
 * @class Form.Group
 * @extends {Component}
 */
export default class FormGroup extends Component {

  /**
   * 组件支持传入的 props 定义
   *
   * @static
   *
   * @property {string} [layout=config.LAYOUT.HORIZONTAL] 布局
   * @property {string} title 可选的配置，组标题
   * @property {string} className 从外部指定的自定义样式类名
   * @property {React.CSSProperties} style 从外部指定的自定义样式
   */
  static propTypes = {
    layout: PropTypes.string,
    title: PropTypes.string,
    // Component 属性
    className: PropTypes.string,
    style: PropTypes.object
  }

  /**
   * 默认的 props
   *
   * @static
   */
  static defaultProps = {
    layout: LAYOUT.HORIZONTAL
  }

  /**
   * 渲染组描述
   */
  renderTitle() {
    const {title} = this.props;

    return (
      <div className={`${CLASS_PREFIX}FormGroup-title`}>
        {title}
      </div>
    )
  }

  /**
   * 渲染子级控件
   */
  renderChildren() {
    const {layout, children} = this.props

    const classes = cx(
      `${CLASS_PREFIX}FormGroup-content`,  // 整体样式
      `${CLASS_PREFIX}${LAYOUT[layout.toUpperCase()] || LAYOUT.HORIZONTAL}`,  // 布局控制
    )

    return (
      <div className={classes}>
        {children}
      </div>
    )
  }

  /**
   * 渲染
   */
  render() {
    const {
      title,
      className,
      style
    } = this.props

    const classes = cx(
      `${CLASS_PREFIX}FormGroup`,  // 整体样式
      className  // 外部样式
    )

    return (
      <div
        className={classes}
        style={style}
      >
        {title && this.renderTitle()}
        {this.renderChildren()}
      </div>
    )
  }
}

/**
 * @file FormControl 组件的 decorator
 *
 * @author Leo Wang(wangkemiao@zhihu.com)
 */

import isEqual from 'lodash.isequal'
import omit from 'lodash.omit'
import React, {Component} from 'react'
import PropTypes from 'prop-types'
import cx from 'classnames'
import kebabCase from 'lodash.kebabcase'
import AsyncValidator from 'async-validator';

import {getStatusFromProps} from '../util'
import {CLASS_PREFIX, STATUS} from '../util/config'

import './Control.css'

export default function connectControl(WrappedComponent) {

  /**
   * 表单元素组件
   *
   * @export FormControl
   */
  return class FormControl extends Component {

    /**
     * 组件接受并使用的 props 定义，所有组件的公共 props
     *
     * 注意在这里会传递给真实控件的 props 只有 id, status
     * 还有两个传过去的实际是 Control 接管的： value, onValueChange，并不是通过 props 传入的
     *
     * @static
     * @memberof Control
     *
     * @property {string} id 命名，此 id 会被传给真实控件，会被 Form 接管
     * @property {string} label 标签
     * @property {Array|Object} rules 校验规则，会被 Form 接管
     * @property {any} value 控件值，会被 Form 接管，直接修改无效
     * @property {any} defaultValue 控件默认值
     * @property {string} status 状态，会被 Form 接管
     * @property {string} message 提示信息，会被 Form 接管
     * @property {boolean} [disabled=false] 是否禁用
     * @property {boolean} [readonly=false] 是否只读
     * @property {string} className 从外部指定的自定义样式类名，不会传递给真实控件
     * @property {React.CSSProperties} style 从外部指定的自定义样式，不会传递给真实控件
     *
     * @property {function} onValueChange 值变化时的相应句柄，来自于 Form hijack
     * @property {function} onStatusChange status 变化时的相应句柄，来自于 Form hijack
     * @property {function} onRulesChange rules 变化时的相应句柄，来自于 Form hijack
     * @property {function} onMessageChange message 变化时的相应句柄，来自于 Form hijack
     */
    static propTypes = {
      id: PropTypes.string,
      label: PropTypes.string,
      className: PropTypes.string,
      style: PropTypes.object,

      // 会被 Form 自动灌入的事件句柄
      onValueChange: PropTypes.func,
      onStatusChange: PropTypes.func,
      onRulesChange: PropTypes.func,
      onMessageChange: PropTypes.func,
    }

    /**
     * 声明一个本类的 mark，当前用于 Form 中查找 Controls
     *
     * @static
     */
    static IS_ZHUI_FORM_CONTROL = true

    /**
     * Creates an instance of Control.
     * @param {Object} props
     * @memberof Control
     */
    constructor(props) {
      super(props)
      this.generateValidator(props)
    }

    /**
     * props 被修改时的处理
     *
     * @param {Object} nextProps
     *
     * @emits {onRulesChange} rule 变化时的响应句柄
     * @emits {onStatusChange} status 变化时的响应句柄
     */
    componentWillReceiveProps(nextProps) {
      const {id, onRulesChange, onStatusChange} = this.props
      // rule 被修改了，同步触发事件
      if (nextProps.rules !== this.props.rules
        || (nextProps.rules && this.props.rules && nextProps.rules.length !== this.props.rules.length)
        || !isEqual(nextProps.rules, this.props.rules)) {
        this.generateValidator(nextProps)
        onRulesChange && onRulesChange({
          payload: {
            [id]: nextProps.rules
          }
        })
      }
      // diabled, readonly 被修改，触发状态更改
      if (nextProps.disabled !== this.props.disabled || nextProps.readonly !== this.props.readonly) {
        const status = getStatusFromProps(nextProps, this.props.status)
        onStatusChange && onStatusChange({
          payload: {
            [id]: {status}
          }
        })
      }
    }

    /**
     * 根据 rules 生成校验使用的 validator
     *
     * @param {Object} [props=this.props]
     */
    generateValidator(props = this.props) {
      const {id, rules} = props
      this.validator = new AsyncValidator({
        [id]: rules || []
      })
    }

    /**
     * 控件值发生修改时的处理
     *
     * @method
     *
     * @param {any} value 修改后的值
     *
     * @emits {onValueChange} 值变化时的响应句柄
     */
    onValueChange = value => {
      const {id, onValueChange} = this.props

      onValueChange && onValueChange({
        payload: {
          [id]: value
        }
      })

      this.validate(value).catch(() => {})
    }

    /**
     * 执行校验
     *
     * @param {any} [value=this.props.value]
     *
     * @emits {onStatusChange} status 变化时的响应句柄
     * @emits {onMessageChange} message 变化时的响应句柄
     *
     * @returns {Promise}
     */
    validate(value = this.props.value) {
      const {id, onStatusChange, onMessageChange} = this.props
      return new Promise((resolve, reject) => {
        // validation
        if (this.validator) {
          this.validator.validate({[id]: value}, {first: true}, (errors, fields) => {
            if (errors) {
              reject(errors)
            }
            else {
              resolve()
            }
          })
        }
        else {
          resolve()
        }
      }).then(() => {
        onStatusChange && onStatusChange({
          payload: {
            [id]: STATUS.PASSED
          }
        })
        onMessageChange && onMessageChange({
          payload: {
            [id]: ''
          }
        })
      }).catch(errors => {
        const payload = {
          [id]: STATUS.FAULTY
        }
        onStatusChange && onStatusChange({
          payload: {
            [id]: STATUS.FAULTY
          }
        })
        onMessageChange && onMessageChange({
          payload: {
            [id]: errors[0].message
          }
        })
        throw payload
      })
    }

    /**
     * 渲染
     *
     * @memberof Control
     */
    render() {
      const {
        id,
        label,
        value,
        status = getStatusFromProps(this.props),
        message,
        className,
        style,
        children,
        ...resetProps
      } = this.props

      const passThroughProps = omit(resetProps, [
        'rules', 'defaultValue',
        'onValueChange', 'onStatusChange', 'onRulesChange', 'onMessageChange'
      ])

      const classes = cx(
        `${CLASS_PREFIX}FormCtrl`,  // 整体样式
        `${CLASS_PREFIX}FormCtrl--is-${status}`,  // 状态类
        className  // 外部样式
      )

      const labelClasses = cx(
        `${CLASS_PREFIX}FormCtrl-label`,  // 整体样式
        `${CLASS_PREFIX}FormCtrl-label--is-${status}`  // 状态类
      )

      const contentClasses = cx(
        `${CLASS_PREFIX}FormCtrl-content`,  // 整体样式
        `${CLASS_PREFIX}FormCtrl-content--is-${status}`  // 状态类
      )

      return (
        <div
          className={classes}
          style={style}
        >
          {label && (
            <label
              htmlFor={id}
              title={typeof label === 'string' ? label : ''}
              className={labelClasses}
            >
              {label}
            </label>
          )}
          <div className={contentClasses}>
            <WrappedComponent
              {...passThroughProps}
              id={id}
              value={value}
              status={status}
              onValueChange={this.onValueChange}
            >
              {children}
            </WrappedComponent>
            {message && <div className={`${CLASS_PREFIX}FormCtrl-message`}>{message}</div>}
          </div>
        </div>
      )
    }
  }
}

/**
 * @file Form
 *
 * @author Leo Wang(wangkemiao@zhihu.com)
 */

import isEqual from 'lodash.isequal'
import AsyncValidator from 'async-validator';
import React, {Component} from 'react'
import PropTypes from 'prop-types'
import cx from 'classnames'

import {getStatusFromProps} from '../util'
import {CLASS_PREFIX, STATUS, LAYOUT} from '../util/config'
import FormGroup from './Group'

import './index.css'

/**
 * 表单组件
 *
 * @export
 * @class Form
 * @extends {Component}
 */
export default class Form extends Component {

  /**
   *
   * 组件接受并使用的 props 定义
   *
   * @static
   * @memberof Form
   *
   * @property {string} [layout=config.LAYOUT.HORIZONTAL] 布局
   * @property {string} className 从外部指定的自定义样式类名
   * @property {React.CSSProperties} style 从外部指定的自定义样式
   * @property {function(formValue: Object)} onSubmit 提交事件响应句柄
   */
  static propTypes = {
    layout: PropTypes.string,
    className: PropTypes.string,
    style: PropTypes.object,
    onSubmit: PropTypes.func
  }

  /**
   * 构造函数
   *
   * @param {any} props
   */
  constructor(props) {
    super(props)
    this.initializeState()
  }

  /**
   * 状态初始化，主要是根据 props.children 去初始化 values, rules, status, messages
   * 生成 state
   */
  initializeState() {
    const {children} = this.props
    const controls = this.getFormControls(children)

    const values = {}
    const rules = {}
    const status = {}
    const messages = {}

    for (let i = 0; i < controls.length; i++) {
      let ctrl = controls[i]
      values[ctrl.props.id] = ctrl.props.defaultValue
      if (ctrl.props.rules) {
        rules[ctrl.props.id] = ctrl.props.rules
      }
      status[ctrl.props.id] = getStatusFromProps(ctrl.props, ctrl.props.status)
      messages[ctrl.props.id] = ''
    }

    this.state = {values, rules, status, messages, initValues: Object.assign({}, values)}
  }

  /**
   * 是否修改了表单项的值
   *
   * @returns {boolean}
   */
  hasChanged() {
    return isEqual(this.state.values, this.state.initValues)
  }

  /**
   * 获取表单下所有的表单控件，被 Control 封过的
   *
   * @param {React.Children} children
   *
   * @returns {Array.<Object>} 子控件，注意是 Object
   */
  getFormControls(children) {
    let controls = []
    const childrenArray = React.Children.toArray(children)
    for (let i = 0; i < childrenArray.length; i++) {
      const child = childrenArray[i]

      if (!React.isValidElement(child)) {
        continue
      }
      if (child.type.IS_ZHUI_FORM_CONTROL) {
        controls.push(child)
      }
      else {
        if (child.props.children) {
          controls = controls.concat(this.getFormControls(child.props.children))
        }
      }
    }
    return controls
  }

  /**
   * 更新值
   *
   * @param {Object} toUpdate 要更新的信息，key 为 id，value 为值
   */
  updateControlValue(toUpdate) {
    this.setState({
      values: Object.assign(this.state.values, toUpdate)
    })
  }

  /**
   * 更新 status
   *
   * @param {Object} toUpdate 要更新的信息，key 为 id，value 为值
   */
  updateControlStatus(toUpdate) {
    this.setState({
      status: Object.assign(this.state.status, toUpdate)
    })
  }

  /**
   * 更新 rules
   *
   * @param {Object} toUpdate 要更新的信息，key 为 id，value 为值
   */
  updateControlRules(toUpdate) {
    this.setState({
      rules: Object.assign(this.state.rules, toUpdate)
    })
  }

  /**
   * 更新 message
   *
   * @param {Object} toUpdate 要更新的信息，key 为 id，value 为值
   */
  updateControlMessages(toUpdate) {
    this.setState({
      messages: Object.assign(this.state.messages, toUpdate)
    })
  }

 /**
  * 传递给子级控件的用于处理值变化的响应句柄
  *
  * @param {Object} e 参数
  * @param {Object} e.payload 变化信息，key 为 id，value 为值
  */
  onValueChange = e => {
    this.updateControlValue(e.payload)
  }

 /**
  * 传递给子级控件的用于处理 status 变化的响应句柄
  *
  * @param {Object} e 参数
  * @param {Object} e.payload 变化信息，key 为 id，value 为值
  */
  onStatusChange = e => {
    this.updateControlStatus(e.payload)
  }

 /**
  * 传递给子级控件的用于处理 rules 变化的响应句柄
  *
  * @param {Object} e 参数
  * @param {Object} e.payload 变化信息，key 为 id，value 为值
  */
  onRulesChange = e => {
    this.updateControlRules(e.payload)
  }

 /**
  * 传递给子级控件的用于处理 message 变化的响应句柄
  *
  * @param {Object} e 参数
  * @param {Object} e.payload 变化信息，key 为 id，value 为值
  */
  onMessageChange = e => {
    this.updateControlMessages(e.payload)
  }

  /**
   * 处理提交
   *
   * @param {Event} e 事件参数
   */
  handleSubmit = e => {
    this.validate().then(() => {
      const {onSubmit} = this.props
      onSubmit(this.state.values)
    }).catch(() => {})
    e.preventDefault()
  }

  /**
   * 整体校验
   *
   * @returns {Promise}
   */
  validate() {
    const validator = new AsyncValidator(this.state.rules)
    const payload = {
      status: {},
      messages: {}
    }
    return new Promise((resolve, reject) => {
      validator.validate(this.state.values, (errors, fields) => {
        if (errors) {
          reject(errors)
        }
        else {
          resolve()
        }
      })
    }).then(() => {
      for (var k in this.state.status) {
        payload.status[k] = STATUS.PASSED
        payload.messages[k] = ''
      }
      this.updateControlStatus(payload.status)
      this.updateControlMessages(payload.messages)
    }).catch(errors => {
      for (let i = 0; i < errors.length; i++) {
        let err = errors[i]
        payload.status[err.field] = STATUS.FAULTY;
        payload.messages[err.field] = err.message
      }

      this.updateControlStatus(payload.status)
      this.updateControlMessages(payload.messages)
      throw payload
    })
  }

  /**
   * 获取被劫持处理后的子级组件们，用于渲染
   *
   * @param {React.Children} children
   *
   * @returns {React.Children}
   */
  getHijackedChildren(children) {

    const {values, status, messages} = this.state
    const toExtendProps = {
      onValueChange: this.onValueChange,
      onStatusChange: this.onStatusChange,
      onRulesChange: this.onRulesChange,
      onMessageChange: this.onMessageChange
    }

    return React.Children.map(children, el => {
      if (React.isValidElement(el)) {
        if (el.type.IS_ZHUI_FORM_CONTROL) {
          const id = el.props.id
          return <el.type {...el.props} {...toExtendProps} value={values[id]} status={status[id]} message={messages[id]}>
            {this.getHijackedChildren(el.props.children)}
          </el.type>
        }
        return <el.type {...el.props}>
          {this.getHijackedChildren(el.props.children)}
        </el.type>
      }
      return el
    })
  }

  /**
   * 渲染
   */
  render() {
    const {
      layout = LAYOUT.HORIZONTAL,
      onSubmit,
      className,
      style,
      children
    } = this.props

    const classes = cx(
      `${CLASS_PREFIX}Form`,  // 整体样式
      `${CLASS_PREFIX}${LAYOUT[layout.toUpperCase()] || LAYOUT.HORIZONTAL}`,  // 布局控制
      className  // 外部样式
    )

    return (
      <form
        className={classes}
        style={style}
        onSubmit={this.handleSubmit}
      >
        {this.getHijackedChildren(children)}
      </form>
    )
  }
}

Form.Group = FormGroup

export const Group = FormGroup

/**
 * @file Input Component
 *
 * @author Leo Wang(wangkemiao@zhihu.com)
 */

import omit from 'lodash.omit'
import React, {Component} from 'react'
import PropTypes from 'prop-types'
import cx from 'classnames'

import {getControlClassNames} from '../util'
import connectControl from '../Form/connectControl'

import './index.css'

/**
 * 输入组件
 *
 * 类似于 html5 中的 input，但只取了输入部分，跟据 type 区分
 * 参考了现有浏览器的实现，屏蔽了部分未实现的 type，并标明原生兼容性
 *
 *  - text: 普通文本，默认值。
 *  - password: 密码。Chr:1.0, FF:1.0, IE:2, Opera:1.0, Safari: 1.0
 *  - email email 格式。Chr:5.0, FF:4.0, IE:10, Opera:10.62, Safari:?
 *  - tel 电话格式。Chr:5.0, FF:4.0, IE:10, Opera:11.01, Safari:?
 *  - url url 地址格式。Chr:5.0, FF:4.0, IE:10, Opera:10.62, Safari:?
 *  - number: 数字类型。Chr:11, FF:29.0, IE:10, Opera:10.62, Safari:Yes
 *
 * 组合式的暂未实现，再考虑
 *  - search: 用于输入搜索字符串的单行文本字段。换行会被从输入的值中自动移除。但这个定义在考虑扩展加入触发搜索行为，有无按钮之类的定制
 *  - range: 范围，可使用 min, max, value, step。Chr:5.0, FF:23.0, IE:10, Opera: 11.01, Safari: Yes
 *
 * @export
 * @class Input
 * @extends {Component}
 */
@connectControl
export default class Input extends Component {

  /**
   * 组件支持传入的 props 定义，继承了 {@link Form.Control#propTypes} 指定的会传入真实控件的 props，故在此只介绍私有的 props
   *
   * @property {string} [type='Input'] 类型，可选值选自 html input 的 type 取值： text|email|number|password|range|search|tel|url
   * @property {string} placeholder 用于提示用户的输入建议信息，原生 IE 10+
   * @property {string} [autoComplete='off'] 是否自动填充，这是浏览器控制的，取值可参考 https://developer.mozilla.org/zh-CN/docs/Web/HTML/Element/input
   * @property {boolean} [autoFocus=false] 是否在加载时自动获得焦点
   * @property {number} tabIndex 使用 tab 键切换时的索引值，原生 Safari:?
   *
   * @property {Function(e:Event)} onInput 输入事件句柄
   */
  static propTypes = {
    type: PropTypes.string,
    placeholder: PropTypes.string,
    autoComplete: PropTypes.bool,
    autoFocus: PropTypes.bool,
    tabIndex: PropTypes.number,
    // 事件
    onInput: PropTypes.func
  }

  /**
   * 默认的 props
   *
   * @static
   * @memberof Input
   */
  static defaultProps = {
    type: 'text',
    onInput(e) {
      e.preventDefault()
    }
  }

  /**
   * 输入时的事件处理
   *
   * @param {React.KeyEvent} e 事件参数
   */
  handleInput = e => {
    const {onValueChange, onInput} = this.props
    // sync value
    onValueChange && onValueChange(e.target.value)
    onInput && onInput(e)
  }

  render() {
    const {
      id,
      children,
      status,
      ...resetProps
    } = this.props

    console.log(resetProps)

    const passThroughProps = omit(resetProps, ['onInput', 'onValueChange'])

    return (
      <input
        {...passThroughProps}
        id={id}
        name={id}  // name 与 id 相同
        className={getControlClassNames('Input', status)}
        onChange={this.handleInput}
      />
    )
  }
}

