      cb(require('./SimpleLineChart').default) // eslint-disable-line global-require
    }
  }, 'simpleLineChart')
}

/**
 * @class AsyncSimpleLineChart
 * @extends {ReactElement}
 */
export default (props) => <AsyncWrapper load={loadLineChart} {...props} />

import {Component, createElement} from 'react'

// webpack will static analyze require.ensure
// see: https://webpack.github.io/docs/code-splitting.html#es6-modules

/**
 * 一个为了支持异步加载 require.ensure 的容易
 */
export default class AsyncWrapper extends Component {

  /**
   * Creates an instance of AsyncWrapper.
   * @param {object} [props=] 传入的 props
   */
  constructor(props) {
    super(props)

    /**
     * @property {object} state 状态数据
     * @property {null} state.component 保存异步 Componennt
     * @property {string} className 可从外部指定样式类名
     */
    this.state = {
      component: null,
    }
  }

  componentDidMount() {
    const {load} = this.props

    load((component) => {
      this.setState({component})
    })
  }

  componentWillUnmount() {
    this.setState({component: null})
  }

  render() {
    const {component} = this.state
    return component ? createElement(component, this.props) : null
  }
}

