    const {
      text, plain, danger, children, className,
      component: Component='button', ...rest
    } = this.props

    return (
      <Component
        className={cx('Button-adv', className, {
          'Button-text': text,
          'Button-plain': plain,
          'Button-danger': danger,
        })}
        {...rest}
      >
        {children}
      </Component>
    )
  }
}

