import PropTypes from 'prop-types'
import React, {Component} from 'react'
import cx from 'classnames'
import {addOrRemoveFromArray} from '../lib/array'
import './CheckboxGroup.css'

/**
 * 组件 CheckboxGroup，用于展现一组 CheckBox
 */
class CheckboxGroup extends Component {

  /**
   * 组件支持传入的 props 定义
   *
   * @property {string} className 可从外部指定样式类名
   * @property {array} value 复选框组的值
   * @property {array} options 复选组内容的配置
   * @property {function(e:Event)} onChange 值发生改变时的响应句柄
   */
  static propTypes = {
    value: PropTypes.array,
    options: PropTypes.array,
  }

  /**
   * 默认的 props
   */
  static defaultProps = {
    value: [],
    options: [],
  }

  /**
   * 点击复选框时的处理方法
   *
   * @emits {onChange} 调用响应句柄 onChange
   */
  handleClickOption = (option) => {
    this.props.onChange(addOrRemoveFromArray(this.props.value, option.value))
  }

  render() {
    const {
      onChange, // eslint-disable-line no-unused-vars
      options, value, className,
      ...rest
    } = this.props

    return (
      <div {...rest} className={cx('CheckboxGroup', className)}>
        {options.map((option => (
          <div
            key={option.value}
            className="CheckboxGroup-item"
            onClick={() => this.handleClickOption(option)}
          >
            <input
              type="checkbox"
              checked={value.includes(option.value)}
              readOnly
            />
            <label className="CheckboxGroup-itemLabel">{option.label}</label>
          </div>
        )))}
      </div>
    )
  }
}

export default CheckboxGroup

import PropTypes from 'prop-types'
