import PropTypes from 'prop-types'
import React, {Component} from 'react'
import {findDOMNode} from 'react-dom'
import cx from 'classnames'
import SVGIcon from '../../ui/SVGIcon'
import EventsJar from '../EventsJar'

export function NormalTh({heading, thClassName}) {
  return <th className={thClassName}>{heading}</th>
}

NormalTh.propTypes = {
  heading: PropTypes.node.isRequired,
  thClassName: PropTypes.string,
}

export function SortTh({heading, thClassName, state, onStateChange}) {
  return (
    <th className={cx('SortTh', thClassName, state !== '' && 'is-active')} onClick={onStateChange}>
      {heading}
      <SVGIcon name="sort" className={cx('SortTh-icon', state !== '' && `is-${state}`)} />
    </th>
  )
}

SortTh.propTypes = {
  heading: PropTypes.node.isRequired,
  thClassName: PropTypes.string,
  state: PropTypes.string.isRequired,
  onStateChange: PropTypes.func.isRequired,
}

export class FilterTh extends Component {
  state = {
    open: false
  }

  static defaultProps = {
    state: ''
  }

  handleDocumentClick = (e) => {
    if (!findDOMNode(this).contains(e.target)) {
      this.setState({open: false})
      return true
    }
    return false
  }

  toggleOpen = () => {
    this.setState({
      open: !this.state.open
    })
  }

  render() {
    const {heading, thClassName, options, state, onStateChange} = this.props
    const {open} = this.state

    return (
      <th
        className={cx('FilterTh', thClassName, state !== '' && 'is-active')}
        onClick={this.toggleOpen}
      >
        {state === '' ? heading : options.find(option => option.value === state).label}
        <SVGIcon name="filter" className="FilterTh-icon"/>
        {open && <EventsJar target={document} onClick={this.handleDocumentClick} />}
        <div className={cx('FilterTh-options', {'FilterTh-options--hidden': !open})}>
          {options.map(option => (
            <div
              key={option.value}
              className="FilterTh-option"
              onClick={() => onStateChange(option.value)}
            >
              {option.label}
            </div>
          ))}
        </div>
      </th>
    )
  }
}

FilterTh.propTypes = {
  options: PropTypes.arrayOf(PropTypes.shape({
    value: PropTypes.string,
    label: PropTypes.string,
  })).isRequired,
  state: PropTypes.string.isRequired,
  heading: PropTypes.node.isRequired,
  thClassName: PropTypes.string,
  onStateChange: PropTypes.func.isRequired,
}

import PropTypes from 'prop-types'
import React from 'react'
import get from 'lodash/get'
import Tabs from '../Tabs'
import SearchBar from '../SearchBar'
import Spinner from '../Spinner'
import {NormalTh, FilterTh, SortTh} from './Th'
import './index.css'

const HeadingMap = {
  normal: NormalTh,
  filter: FilterTh,
  sort: SortTh,
}

function DataTableThead({renderers}) {
  return (
    <thead>
      <tr>
        {renderers.map((render) => {
          const Heading = HeadingMap[render.type || 'normal']
          return <Heading key={render.heading} {...render} />
        })}
      </tr>
    </thead>
  )
}

function DataTableTbody({renderers, data, rowProps}) {
  return (
    <tbody>
      {data.map((item, index) => (
        <tr
          key={index}
          {...rowProps(item)}
        >
          {renderers.map(renderer => (
            <td key={renderer.heading}>
              {renderer.dataField ? get(item, renderer.dataField) : renderer.render(item)}
            </td>
          ))}
        </tr>
      ))}
    </tbody>
  )
}

function Table({renderers, data, rowProps}) {
  return (
    <table className="DataTable-table">
      <DataTableThead renderers={renderers} />
      <DataTableTbody renderers={renderers} data={data} rowProps={rowProps} />
    </table>
  )
}

Table.propTypes = {
  data: PropTypes.arrayOf(PropTypes.object).isRequired,
  renderers: PropTypes.arrayOf(PropTypes.shape({
    heading: PropTypes.node.isRequired,
    thClassName: PropTypes.string,
    dataField: PropTypes.string,
    render: PropTypes.func,
    type: PropTypes.oneOf(['normal', 'filter', 'sort']),
    state: PropTypes.string,
    onStateChange: PropTypes.func,
  })).isRequired,
  rowProps: PropTypes.func,
}

export default function DataTable(props) {
  const {
    data, renderers, isLoading, rowProps,
    tabs, selectedTab, onTabChange,
    searchText, onSearchTextChange, searchPlaceholder,
  } = props

  return (
    <div className="DataTable">
      {Boolean(tabs) &&
        <div className="DataTable-actions">
          <Tabs
            className="DataTable-Tabs"
            tabs={tabs}
            selectedTab={selectedTab}
            onTabChange={onTabChange}
          />
          {Boolean(onSearchTextChange) &&
            <SearchBar
              className="DataTable-SearchBar"
              value={searchText}
              onChange={onSearchTextChange}
              placeholder={searchPlaceholder}
            />
          }
        </div>
      }
      <Table renderers={renderers} data={data} rowProps={rowProps} />
      {(!isLoading && (!data || !data.length)) && <div className="DataTable-placeholder">没有内容</div>}
      {isLoading && (
        <div className="DataTable-loading">
          <Spinner className="DataTable-spinner" />
        </div>
      )}
    </div>
  )
}

DataTable.defaultProps = {
  rowProps() {}
}

DataTable.propTypes = {
  isLoading: PropTypes.bool,
  data: PropTypes.arrayOf(PropTypes.object).isRequired,
  renderers: PropTypes.arrayOf(PropTypes.shape({
    heading: PropTypes.node.isRequired,
    thClassName: PropTypes.string,
    dataField: PropTypes.string,
    render: PropTypes.func,
    type: PropTypes.oneOf(['normal', 'filter', 'sort']),
    state: PropTypes.string,
    onStateChange: PropTypes.func,
  })).isRequired,
  rowProps: PropTypes.func,
  tabs: PropTypes.arrayOf(PropTypes.shape({
    value: PropTypes.string,
    label: PropTypes.string,
    meta: PropTypes.node,
  })),
  selectedTab: PropTypes.string,
  onTabChange: PropTypes.func,
  searchText: PropTypes.string,
  onSearchTextChange: PropTypes.func,
  placeholder: PropTypes.string,
}

import PropTypes from 'prop-types'
import React, {Component} from 'react'
import cx from 'classnames'
import Portal from '../Portal'
import EventsJar from '../EventsJar'
import IconButton from '../IconButton'
import {ESC} from '../../constants/keycode'

const DialogHeader = ({title, subtitle}) => (
  <div className="Dialog-header">
    {(title || subtitle) && [
      <div key="title" className="Dialog-title">{title}</div>,
      <div key="subtitle" className="Dialog-subtitle">{subtitle}</div>,
    ]}
  </div>
)

const DialogBody = ({children}) => (
  <div className="Dialog-body">
    {children}
  </div>
)

class Dialog extends Component {
  static propTypes = {
    children: PropTypes.node,
    title: PropTypes.string,
    onClose: PropTypes.func,
    plain: PropTypes.bool,
    isOpen: PropTypes.bool.isRequired,
  }

  static defaultProps = {
    isOpen: false,
    plain: false,
  }

  handleGlobalKeyDown = e => {
    if (e.keyCode === ESC) {
      this.props.onClose()
    }
  }

  handleClickDocument = e => {
    if (this.content && !this.content.contains(e.target)) {
      this.props.onClose()
    }
  }

  render() {
    const {className, children, isOpen, onClose, plain} = this.props

    return (
      <Portal className="Dialog">
        {isOpen && (
          <div className="Dialog-overlay">
            <div
              ref={(node) => { this.content = node }}
              className={cx('Dialog-content', className, {'Dialog--plain': plain})}
            >
              <IconButton className="Dialog-close" name="close" fill="#fff" size={12} onClick={onClose} />
              {children}
              <EventsJar
                target={document}
                onKeyDown={this.handleGlobalKeyDown}
                onClick={this.handleClickDocument}
              />
            </div>
          </div>
        )}
      </Portal>
    )
  }
}

Dialog.Header = DialogHeader
Dialog.Body = DialogBody

export default Dialog

import Dialog from './Dialog'
import staticize from './staticize'
import ConfirmDialog from './ConfirmDialog'
import './index.css'

const confirm = staticize(ConfirmDialog)

export {
