}
export default Dialog

import React from 'react'
import ReactDOM from 'react-dom'
import once from 'lodash/once'
import closeOnRouteChange from '../../hocs/closeOnRouteChange'

const staticize = Dialog => (message, options) => (
  new Promise(resolve => {
    const holder = document.createElement('div')
    document.body.appendChild(holder)

    const cleanup = once(() => {
      setTimeout(() => {
        ReactDOM.unmountComponentAtNode(holder)
        document.body.removeChild(holder)
      })
    })

    const handleConfirm = () => {
      cleanup()
      resolve()
    }

    const handleClose = () => {
      cleanup()
    }

    const EnhanceDialog = closeOnRouteChange(Dialog)

    ReactDOM.render((
      <EnhanceDialog
        {...options}
        isOpen
        message={message}
        onClose={handleClose}
        onConfirm={handleConfirm}
      />
    ), holder)
  })
)

export default staticize

import PropTypes from 'prop-types'
import React, {Component, Children, cloneElement} from 'react'
import {findDOMNode} from 'react-dom'
import Button from './Button'
import Popover from './Popover'
import EventsJar from './EventsJar/index'
import SVGIcon from './SVGIcon'
import './Dropdown.css'

/**
 * 下拉（按钮）列表，更准确的说，类似于 Menu，与 {@link Select} 的区别：
 *  - 长得更像是菜单项
 *  - 不可能支持 复选
 *  - 目的是执行命令，不会更新展示
 */
class Dropdown extends Component {

  /**
   * 组件支持传入的 props 定义
   *
   * @property {ReactElement} children 内嵌子组件，用这个展示内容
   * @property {string} className 可从外部指定样式类名
   * @property {string} title 标题
   * @property {boolean} [disabled=false] 是否禁用
   * @property {boolean} plain 这个是给内置 Button 使用的，可参考 {@link Button.propTypes}
   */
  static propTypes = {
    children: PropTypes.node,
    className: PropTypes.string,
    title: PropTypes.string,
    disabled: PropTypes.bool,
    plain: PropTypes.bool,
  }

  /**
   * 内置状态对象
   */
  state = {
    showOptions: false
  }

  /**
   * 处理下拉项浮层的展现/隐藏切换
   */
  handleToggleDropdown = () => {
    this.setState({showOptions: !this.state.showOptions})
  }

  /**
   * 点击文档其他位置时隐藏下拉项浮层
   *
   * @param {Event} e 事件
   */
  handleDocumentClick = (e) => {
    if (!findDOMNode(this).contains(e.target)) {
      this.close()
    }
  }

  /**
   * 隐藏下拉项浮层
   */
  close = () => {
    this.setState({showOptions: false})
  }

  render() {
    const {children, title, disabled, plain, ...rest} = this.props
    const {showOptions} = this.state

    return (
      <div {...rest} className="Dropdown">
        <Button
          className="Dropdown-button"
          plain={plain}
          disabled={disabled}
          onClick={this.handleToggleDropdown}
        >
          {title}
          <SVGIcon
            name="arrow"
            size={24}
            fill="#0f88eb"
            style={{transform: `rotate(${showOptions ? -90 : 90}deg)`}}
          />
        </Button>
        <Popover isOpen={showOptions}>
          <EventsJar
            target={document}
            onClick={this.handleDocumentClick}
          />
          <div className="Dropdown-menu">
            {Children.map(Children.toArray(children), (child) =>
              cloneElement(child, {
                onClick: () => {
                  if (child.props.onClick) { child.props.onClick() }
                  this.close()
                }
              })
            )}
          </div>
        </Popover>
      </div>
    )
  }
}

export default Dropdown

import PropTypes from 'prop-types'
import {Component} from 'react'

const EVENT_REGEX = /^on\w+$/

class EventsJar extends Component {
  static propTypes = {
    target: PropTypes.object,
  }

  static defaultProps = {
    target: global.window,
  }

  componentDidMount() {
    if (this.props.target) {
      this.handlerJar = {}
      this.registerEvents(this.props)
    }
  }

  componentWillUnmount() {
    if (this.handlerJar) {
      this.dispose()
    }
  }

  dispose() {
    Object.keys(this.handlerJar).forEach(name => {
      this.props.target.removeEventListener(name.slice(2), this.handlerJar[name])
    })
    this.handlerJar = null
  }

  registerEvents({target, ...events}) {
    if (!this.handlerJar) {
      return
    }
    Object.keys(events).forEach(key => {
      const name = key.toLowerCase()
      const handler = events[key]

      if (name in target &&
        EVENT_REGEX.test(name) &&
        typeof handler === 'function' &&
        !(name in this.handlerJar)
      ) {
        this.handlerJar[name] = handler
        target.addEventListener(name.slice(2), handler)
      }
    })
  }

  render() {
    return null
  }
}

export default EventsJar

