import PropTypes from 'prop-types'
import React, {Component} from 'react'
import cx from 'classnames'

/**
 * 一个滚动组件，在滚动至底部时会触发相应句柄以便进行异步加载处理
 */
class InfiniteScroll extends Component {

  /**
   * 默认的 props
   */
  static defaultProps = {
    distance: 0, /* delta distance of detecting if reaching the bottom */
    onReachBottom() {},
  }

  /**
   * 组件支持传入的 props 定义
   *
   * @property {number} [distance=0] 定义距离底部多少像素即视为到达底部
   * @property {function} onReachBottom 到达底部时的响应处理句柄
   */
  static propTypes = {
    distance: PropTypes.number.isRequired,
    onReachBottom: PropTypes.func,
  }

  componentDidMount() {
    if (this.infinite) {
      this.infinite.addEventListener('scroll', this.handleScroll)
    }
  }

  componentWillUnmount() {
    if (this.infinite) {
      this.infinite.removeEventListener('scroll', this.handleScroll)
    }
  }

  /**
   * 滚动事件处理函数
   *
   * @emits {onReachBottom} 调用响应句柄 onReachBottom
   */
  handleScroll = () => {
    if (this.hasReachedBottom) {
      this.props.onReachBottom()
    }
  }

  /**
   * 计算属性 - 是否到达底部
   *
   * @readonly
   * @type {boolean}
   */
  get hasReachedBottom() {
    const {scrollHeight, scrollTop, clientHeight} = this.infinite
    return scrollHeight - scrollTop <= clientHeight + this.props.distance
  }

  render() {
    const {
      children, className,
      distance, onReachBottom, // eslint-disable-line no-unused-vars
      ...rest
    } = this.props

    return (
      <div
        {...rest}
        className={cx('InfiniteScroll', className)}
        ref={node => { this.infinite = node}}
      >
        {children}
      </div>
    )
  }
}

export default InfiniteScroll

import PropTypes from 'prop-types'
import React, {Component} from 'react'
import cx from 'classnames'
