/**
 * @file util 方法
 *
 * @author Leo Wang(wangkemiao@zhihu.com)
 */

import {CLASS_PREFIX, STATUS} from './config'
import cx from 'classnames'

/**
 * 根据 props 获得对应的 status
 *
 * @export
 * @param {Object} props
 */
export function getStatusFromProps(props = {}, currentStatus = STATUS.NORMAL) {
  let status = currentStatus
  if (props.disabled) {
    status = STATUS.DISABLED
  }
  else if (props.readonly){
    status = STATUS.READONLY
  }
  return status
}

/**
 * 获取 Control 的样式类名
 *
 * @export
 * @param {string} name 控件类名，注意首字母要大写
 * @param {string} [status=config.STATUS.NORMAL] 控件状态
 */
export function getControlClassNames(name, status = STATUS.NORMAL) {
  return cx(
    `${CLASS_PREFIX}${name}`,  // 控件样式
    `${CLASS_PREFIX}Input--is-${status}`  // 状态样式
  )
}
